mapper.update:
update sys_role
set role_name = #{roleName},
    role_sort = #{roleSort},
    update_by = #{updateBy}
where role_id = #{roleId}

update sys_role
set deleted = 1
where role_id = #{roleId}

mapper.select:
SELECT role_id, role_name, role_sort, create_by, create_time, update_by, update_time FROM sys_role WHERE deleted = 0
and role_name = #{params.roleName}
order by role_sort limit #{pageNumber},#{pageSize}

SELECT role_id, role_name, role_sort, create_by, create_time, update_by, update_time FROM sys_role WHERE role_id in (
#{id}
)
