mapper.update:
update sys_user
set deleted = 1
where id = #{}

update sys_user
set login_time = NOW()
where username = #{username}

update sys_user
set status = #{status}
where id = #{id}

mapper.select:
select id, username, nick_name, header, note, create_time, login_time, status from sys_user where deleted = 0
and username = #{param.username}
and nick_name like concat('%',#{params.nickName),'%')
and status = #{status}
limit #{index}, #{pageSize)

select count(*) from sys_user where deleted = 0
and username = #{param.username}
and nick_name like concat('%',#{params.nickName),'%')
and status = #{status}
